 <?php 
 	include('login/signup.php');
	if (isset($_POST['sub'])) {
		if ($_POST['users_num']=='') {
			echo "<script>alert('用户名不能为空！');</script>";
		}else if($_POST['password']==''){
			echo "<script>alert('密码不能为空！');</script>";
		}else if($_POST['inputverify']==''){
			echo "<script>alert('验证码不能为空！');</script>";
		}else{
			if (@$_POST['inputverify'] == $_SESSION['code']) {
				if (preg_match('/^\d{12}$/', $_POST['users_num'])) {
					if ($_POST['users_num']==$row['stu_num']) {
						if ($_POST['password']==$row['password']) {
							echo "<script>window.location.href='user.php';</script>";
						}else {
							echo "<script>alert('密码错误！');</script>";
						}
					}else {
						echo "<script>alert('账号不存在！');</script>";
					}
				}else if (preg_match('/^[A-Za-z]+$/', $_POST['users_num'])) {
					if ($_POST['users_num']==$row['user']) {
						if ($_POST['password']==$row['password']) {
							echo "<script>window.location.href='admin.php';</script>";
						}else {
							echo "<script>alert('密码错误！');</script>";
						}
					}else {
						echo "<script>alert('账号不存在！');</script>";
					}
				}else{
					echo "<script>alert('账号不存在！');</script>";
				}
			}else{
				echo "<script>alert('验证码错误！');</script>";
				echo "<script>window.location.href='login.php';</script>";
			}
		}
	}else {
		session_unset();
// session_destroy();
	}
 ?>    
